field.query:
SELECT position, CONCAT(IF(position='','---',position),' -: ', GROUP_CONCAT(title ORDER BY ordering SEPARATOR ', ')) AS title FROM (
                            SELECT position, ordering, CONCAT(title,' (',id,': ',module,')', IF(published=1,'â˜º','â™¯'),IF(showtitle=1,'+','-')) AS title FROM #__modules WHERE client_id=0 /*AND module!='mod_multi'*/ AND position!='' ORDER BY ordering
                        ) AS tbl GROUP BY position;
